import React, { useEffect, useRef, useState } from "react";
import * as THREE from "three";
import axios from "axios";
import CountryModal from "./CountryModal";

const Globe = () => {
  const globeRef = useRef();
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState(null);
  const raycaster = new THREE.Raycaster();
  const mouse = new THREE.Vector2();
  let renderer, scene, camera, globe;
  let isDragging = false;
  let previousMousePosition = { x: 0, y: 0 };

  useEffect(() => {
    // Fetch country data from the API
    axios
      .get("http://localhost:3001/countries")
      .then((response) => {
        setCountries(response.data);
      })
      .catch((error) => {
        console.error("Error fetching countries", error);
      });

    // Only create renderer and scene once
    if (!renderer && !scene && !camera) {
      // Set up the Three.js scene
      scene = new THREE.Scene();
      camera = new THREE.PerspectiveCamera(
        75,
        window.innerWidth / window.innerHeight,
        0.1,
        1000
      );
      renderer = new THREE.WebGLRenderer();
      renderer.setSize(window.innerWidth, window.innerHeight);
      globeRef.current.appendChild(renderer.domElement);

      // Create a sphere for the globe
      const geometry = new THREE.SphereGeometry(5, 32, 32);
      const material = new THREE.MeshBasicMaterial({
        color: 0xffffff,
        wireframe: true,
      });
      globe = new THREE.Mesh(geometry, material);
      scene.add(globe);

      camera.position.z = 10;

      // Animate the globe (initial animation stops after user interaction)
      const animate = () => {
        requestAnimationFrame(animate);
        if (!isDragging) {
          globe.rotation.y += 0.01; // Keep spinning slowly unless dragged
        }
        renderer.render(scene, camera);
      };
      animate();
    }

    // Detect mouse movements for dragging
    const handleMouseDown = (event) => {
      isDragging = true;
    };

    const handleMouseMove = (event) => {
      if (isDragging) {
        const deltaMove = {
          x: event.offsetX - previousMousePosition.x,
          y: event.offsetY - previousMousePosition.y,
        };

        globe.rotation.y += deltaMove.x * 0.005; // Rotate horizontally
        globe.rotation.x += deltaMove.y * 0.005; // Rotate vertically

        previousMousePosition = {
          x: event.offsetX,
          y: event.offsetY,
        };
      }
    };

    const handleMouseUp = () => {
      isDragging = false;
    };

    const handleMouseOut = () => {
      isDragging = false;
    };

    globeRef.current.addEventListener("mousedown", handleMouseDown);
    globeRef.current.addEventListener("mousemove", handleMouseMove);
    window.addEventListener("mouseup", handleMouseUp);
    window.addEventListener("mouseout", handleMouseOut);

    // Clean up event listeners
    return () => {
      globeRef.current.removeEventListener("mousedown", handleMouseDown);
      globeRef.current.removeEventListener("mousemove", handleMouseMove);
      window.removeEventListener("mouseup", handleMouseUp);
      window.removeEventListener("mouseout", handleMouseOut);
    };
  }, [countries]);

  return (
    <div>
      <div ref={globeRef} />
      <CountryModal
        isOpen={!!selectedCountry}
        countryData={selectedCountry}
        onClose={() => setSelectedCountry(null)}
      />
    </div>
  );
};

export default Globe;
