import React, { useEffect, useRef, useState } from "react";
import * as THREE from "three";
import axios from "axios";
import CountryModal from "./CountryModal";

const Globe = () => {
  const globeRef = useRef();
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState(null);
  const raycaster = new THREE.Raycaster();
  const mouse = new THREE.Vector2();
  let renderer, scene, camera, globe;

  useEffect(() => {
    // Fetch country visit data from the API
    axios
      .get("http://localhost:3001/countries")
      .then((response) => {
        setCountries(response.data);
      })
      .catch((error) => {
        console.error("Error fetching countries", error);
      });

    // Set up the Three.js scene
    scene = new THREE.Scene();
    camera = new THREE.PerspectiveCamera(
      75,
      window.innerWidth / window.innerHeight,
      0.1,
      1000
    );
    renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    globeRef.current.appendChild(renderer.domElement);

    // Create a sphere for the globe
    const geometry = new THREE.SphereGeometry(5, 32, 32);
    const material = new THREE.MeshBasicMaterial({
      color: 0xffffff,
      wireframe: true,
    });
    globe = new THREE.Mesh(geometry, material);
    scene.add(globe);

    // Load GeoJSON data and render countries
    const loader = new THREE.FileLoader();
    loader.load(countriesGeoJSON, function (data) {
      const geoData = JSON.parse(data);

      geoData.features.forEach((feature) => {
        const countryName = feature.properties.ADMIN; // 'ADMIN' is the property name for country names in Natural Earth GeoJSON

        // Find if the country has been visited
        const countryData = countries.find((c) => c.name === countryName);
        const visited = countryData ? countryData.visited : false;

        // Create country shape from GeoJSON
        const shape = createCountryShape(feature);

        // Assign color based on visit status (green for visited, red for not visited)
        const countryMaterial = new THREE.MeshBasicMaterial({
          color: visited ? 0x00ff00 : 0xff0000,
          opacity: 0.7,
          transparent: true,
        });

        const countryMesh = new THREE.Mesh(shape, countryMaterial);
        globe.add(countryMesh);
      });
    });

    camera.position.z = 10;

    // Detect clicks on the globe
    const handleMouseClick = (event) => {
      mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
      mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;
      raycaster.setFromCamera(mouse, camera);

      const intersects = raycaster.intersectObjects(scene.children);
      if (intersects.length > 0) {
        const clickedCountry = intersects[0].object; // Get the clicked country
        setSelectedCountry(clickedCountry.userData); // Assuming country data is in userData
      }
    };

    window.addEventListener("click", handleMouseClick);

    // Animate the globe
    const animate = () => {
      requestAnimationFrame(animate);
      renderer.render(scene, camera);
    };
    animate();

    // Clean up event listener on unmount
    return () => {
      window.removeEventListener("click", handleMouseClick);
    };
  }, [countries]);

  // Function to create country shape from GeoJSON data
  const createCountryShape = (feature) => {
    const shape = new THREE.Shape();

    feature.geometry.coordinates[0].forEach((coordinate, index) => {
      const [x, y] = coordinate;
      if (index === 0) {
        shape.moveTo(x, y);
      } else {
        shape.lineTo(x, y);
      }
    });

    return new THREE.ShapeGeometry(shape);
  };

  return (
    <div>
      <div ref={globeRef} />
      <CountryModal
        isOpen={!!selectedCountry}
        countryData={selectedCountry}
        onClose={() => setSelectedCountry(null)}
      />
    </div>
  );
};

export default Globe;
