import React from "react";
import Modal from "react-modal";

const CountryModal = ({ isOpen, countryData, onClose }) => {
  if (!countryData) return null; // If no country data, don't render the modal

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onClose}
      contentLabel="Country Details"
    >
      <h2>{countryData.name}</h2>
      <p>
        Cities Visited: {countryData.cities.map((city) => city.name).join(", ")}
      </p>
      <p>Recommendations: {countryData.recommendations}</p>
      <p>Highlights: {countryData.highlights}</p>
      <p>Dislikes: {countryData.dislikes}</p>
      <button onClick={onClose}>Close</button>
    </Modal>
  );
};

export default CountryModal;
