import React, { useEffect, useRef, useState } from "react";
import * as THREE from "three";
import CountryModal from "./CountryModal";

const Globe = () => {
  const globeRef = useRef();
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState(null);

  const raycaster = new THREE.Raycaster();
  const mouse = new THREE.Vector2();

  // Use useRef to persist these objects across renders
  const renderer = useRef(null);
  const scene = useRef(null);
  const camera = useRef(null);
  const globe = useRef(null);

  useEffect(() => {
    // Sample country data for testing
    const sampleCountries = [
      {
        name: "Japan",
        visited: true,
        cities: [{ name: "Tokyo" }, { name: "Osaka" }],
        recommendations: "Visit Shibuya and Mt. Fuji",
        highlights: "Beautiful landscapes and culture",
        dislikes: "Crowded in some areas",
      },
      {
        name: "Germany",
        visited: false,
        cities: [],
        recommendations: "",
        highlights: "Rich history and architecture",
        dislikes: "",
      },
      {
        name: "Brazil",
        visited: true,
        cities: [{ name: "Rio de Janeiro" }, { name: "São Paulo" }],
        recommendations: "Visit Christ the Redeemer and Copacabana beach",
        highlights: "Vibrant culture and beautiful beaches",
        dislikes: "High levels of traffic",
      },
    ];

    setCountries(sampleCountries); // Use sample data instead of fetching

    // Set up the Three.js scene (ensure it's only set up once)
    if (!renderer.current && !scene.current && !camera.current) {
      // Set up the scene
      scene.current = new THREE.Scene();
      camera.current = new THREE.PerspectiveCamera(
        75,
        window.innerWidth / window.innerHeight,
        0.1,
        1000
      );
      renderer.current = new THREE.WebGLRenderer();
      renderer.current.setSize(window.innerWidth, window.innerHeight);
      globeRef.current.appendChild(renderer.current.domElement);

      // Create a sphere for the globe
      const geometry = new THREE.SphereGeometry(5, 32, 32);
      const material = new THREE.MeshBasicMaterial({
        color: 0xffffff,
        wireframe: true,
      });
      globe.current = new THREE.Mesh(geometry, material);
      scene.current.add(globe.current);

      camera.current.position.z = 10;

      // Animation function to spin the globe
      const animate = () => {
        requestAnimationFrame(animate);

        // Rotate the globe slightly on every frame
        globe.current.rotation.y += 0.01;

        renderer.current.render(scene.current, camera.current);
      };
      animate();
    }

    // Detect clicks on the globe
    const handleMouseClick = (event) => {
      mouse.x = (event.clientX / window.innerWidth) * 2 - 1;
      mouse.y = -(event.clientY / window.innerHeight) * 2 + 1;
      raycaster.setFromCamera(mouse, camera.current);

      const intersects = raycaster.intersectObjects(scene.current.children);
      if (intersects.length > 0) {
        const clickedCountry = countries[0]; // For now, simulate clicking the first country (Japan)
        setSelectedCountry(clickedCountry); // Set this as the selected country
      }
    };

    window.addEventListener("click", handleMouseClick);

    // Clean up event listener on unmount
    return () => {
      window.removeEventListener("click", handleMouseClick);
    };
  }, [countries]);

  return (
    <div>
      <div ref={globeRef} />
      <CountryModal
        isOpen={!!selectedCountry}
        countryData={selectedCountry}
        onClose={() => setSelectedCountry(null)}
      />
    </div>
  );
};

export default Globe;
